class Custom_DbForUpdate extends Zend_Queue_Adapter_Db { /** * @see Code in tests/Zend/Queue/Custom/DbForUpdate.php * * Custom_DbForUpdate verwendet SELECT ... FOR UPDATE um seine Zeilen zu * finden. Das ist besser um die gewünschten Zeilen zu erstellen als der * existierende Code. * * Trotzdem haben nicht alle Datenbanken SELECT ... FOR UPDATE als Feature. * * Notiz: Das wurde später konvertiert um eine Option für * Zend_Queue_Adapter_Db zu sein. * * Dieser Code ist trotzdem ein gutes Beispiel. */ }
$options = array( 'name' => 'queue1', 'driverOptions' => array( 'host' => '127.0.0.1', 'port' => '3306', 'username' => 'queue', 'password' => 'queue', 'dbname' => 'queue', 'type' => 'pdo_mysql' ) );
$adapter = new Custom_DbForUpdate($options); $queue = new Zend_Queue($adapter, $options);

$adapter = new MyCustom_Adapter($options);
$queue   = new Zend_Queue($options);
$queue->setAdapter($adapter);
echo "Adapter: ", get_class($queue->getAdapter()), "\n";
