example.programlisting:
$currency = new Zend_Currency(
    array(
        'value' => 100,
    )
);

print $currency; // Pourrait afficher '$ 100'

$currency = new Zend_Currency(
    array(
        'value'   => 100,
        'display' => Zend_Currency::USE_SHORTNAME,
    )
);

print $currency; // Pourrait retourner 'USD 100'
