// dans le bootstrap
$locale = new Zend_Locale('de_AT');
Zend_Registry::set('Zend_Locale', $locale);

// quelque part dans l'application
$currency = new Zend_Currency();

$currency = new Zend_Currency('US');

// Voir le réglage courant qui vaut 'en_US'
// var_dump($currency);
