sect1.programlisting:
$options = array(
    'name'          => 'queue1',
    'driverOptions' => array(
        'host'      => '127.0.0.1',
        'port'      => '3306',
        'username'  => 'queue',
        'password'  => 'queue',
        'dbname'    => 'queue',
        'type'      => 'pdo_mysql'
    )
);

// データベース待ち行列を作成します。
// Zend_Queue はクラス名として Zend_Queue_Adapter_ の後に 'Db' を付加します。
$queue = new Zend_Queue('Db', $options);
