example.programlisting:
$currency = new Zend_Currency(
    array(
        'value' => 100,
    )
);

print $currency; // Könnte '$ 100' zurückgeben

$currency = new Zend_Currency(
    array(
        'value'    => 100,
        'position' => Zend_Currency::RIGHT,
    )
);

print $currency; // Könnte '100 $' zurückgeben
