sect4.programlisting:
/* * Navigations Container (config/array)
* Jedes Element im Array wird an Zend_Navigation_Page::factory() * übergeben wenn der unten angezeigt Navigations Container * erstellt wird. */ $pages = array( array( 'label' => 'Home', 'title' => 'Geh zu Home', 'module' => 'default', 'controller' => 'index', 'action' => 'index', 'order' => -100 // Sicherstellen das Home die erste Seite ist ), array( 'label' => 'Spezielles Angebot nur diese Woche!', 'module' => 'store', 'controller' => 'offer', 'action' => 'amazing', 'visible' => false // nicht sichtbar ), array( 'label' => 'Produkte', 'module' => 'products', 'controller' => 'index', 'action' => 'index', 'pages' => array( array( 'label' => 'Foo Server', 'module' => 'products', 'controller' => 'server', 'action' => 'index', 'pages' => array( array( 'label' => 'FAQ', 'module' => 'products', 'controller' => 'server', 'action' => 'faq', 'rel' => array( 'canonical' => 'http://www.example.com/?page=faq', 'alternate' => array( 'module' => 'products', 'controller' => 'server', 'action' => 'faq', 'params' => array('format' => 'xml') ) ) ), array( 'label' => 'Editionen', 'module' => 'products', 'controller' => 'server', 'action' => 'editions' ), array( 'label' => 'System Anforderungen', 'module' => 'products', 'controller' => 'server', 'action' => 'requirements' ) ) ), array( 'label' => 'Foo Studio', 'module' => 'products', 'controller' => 'studio', 'action' => 'index', 'pages' => array( array( 'label' => 'Kunden Stories', 'module' => 'products', 'controller' => 'studio', 'action' => 'customers' ), array( 'label' => 'Support', 'module' => 'prodcts', 'controller' => 'studio', 'action' => 'support' ) ) ) ) ), array( 'label' => 'Firma', 'title' => 'Über uns', 'module' => 'company', 'controller' => 'about', 'action' => 'index', 'pages' => array( array( 'label' => 'Investor Relations', 'module' => 'company', 'controller' => 'about', 'action' => 'investors' ), array( 'label' => 'News', 'class' => 'rss', // Klasse 'module' => 'company', 'controller' => 'news', 'action' => 'index', 'pages' => array( array( 'label' => 'Für die Presse', 'module' => 'company', 'controller' => 'news', 'action' => 'press' ), array( 'label' => 'Archiv', 'route' => 'archive', // Route 'module' => 'company', 'controller' => 'news', 'action' => 'archive' ) ) ) ) ), array( 'label' => 'Community', 'module' => 'community', 'controller' => 'index', 'action' => 'index', 'pages' => array( array( 'label' => 'Mein Account', 'module' => 'community', 'controller' => 'account', 'action' => 'index', 'resource' => 'mvc:community.account' // Ressource ), array( 'label' => 'Forum', 'uri' => 'http://forums.example.com/', 'class' => 'external' // Klasse ) ) ), array( 'label' => 'Administration', 'module' => 'admin', 'controller' => 'index', 'action' => 'index', 'resource' => 'mvc:admin', // Ressource 'pages' => array( array( 'label' => 'Neuen Artikel schreiben', 'module' => 'admin', 'controller' => 'post', 'aciton' => 'write' ) ) ) );
// Container von einem Array erstellen $container = new Zend_Navigation($pages);
// Den Container im Proxy Helfer speichern $view->getHelper('navigation')->setContainer($container);
// ...oder einfach: $view->navigation($container);
// ...oder ihn einfach in der Registry speichern: Zend_Registry::set('Zend_Navigation', $container);

// Router Setup (Standardrouten und 'archive' Route):
$front = Zend_Controller_Front::getInstance();
$router = $front->getRouter();
$router->addDefaultRoutes();
$router->addRoute(
    'archive',
    new Zend_Controller_Router_Route(
        '/archive/:year',
        array(
            'module'     => 'company',
            'controller' => 'news',
            'action'     => 'archive',
            'year'       => (int) date('Y') - 1
        ),
        array('year' => '\d+')
    )
);

// ACL Setup:
$acl = new Zend_Acl();
$acl->addRole(new Zend_Acl_Role('member'));
$acl->addRole(new Zend_Acl_Role('admin'));
$acl->add(new Zend_Acl_Resource('mvc:admin'));
$acl->add(new Zend_Acl_Resource('mvc:community.account'));
$acl->allow('member', 'mvc:community.account');
$acl->allow('admin', null);

// ACL und Rolle im Proxy Helfer speichern:
$view->navigation()->setAcl($acl)->setRole('member');

// ...oder ein standard ACL und Rolle statisch setzen:
Zend_View_Helper_Navigation_HelperAbstract::setDefaultAcl($acl);
Zend_View_Helper_Navigation_HelperAbstract::setDefaultRole('member');

