example.programlisting:
$currency = new Zend_Currency(
    array(
        'value' => 100,
    )
);

print $currency; // Could return '$ 100'

$currency = new Zend_Currency(
    array(
        'value'   => 100,
        'display' => Zend_Currency::USE_SHORTNAME,
    )
);

print $currency; // Could return 'USD 100'

$currency = new Zend_Currency(
    array(
        'value' => 100,
        'name'  => 'Dollar',
    )
);

print $currency; // Could return 'Dollar 100'

$currency = new Zend_Currency(
    array(
        'value'    => 100,
        'symbol' => '$$$',
    )
);

print $currency; // Could return '$$$ 100'
